
CREATE TABLE `payment` ( `id_payment` INT(11) NOT NULL AUTO_INCREMENT ,  `invoice` VARCHAR(32) NOT NULL ,  `merchant_id` VARCHAR(32) NOT NULL ,  `item_name` VARCHAR(155) NOT NULL ,  `amount` FLOAT NOT NULL ,  `payment_type` ENUM('virtual_account','credit_card') NOT NULL DEFAULT 'virtual_account' ,  `customer_name` VARCHAR(55) NOT NULL ,  `status` ENUM('pending','paid','failed') NOT NULL DEFAULT 'pending' ,  `created_at` DATETIME NOT NULL ,  `modified_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ,    PRIMARY KEY  (`id_payment`)) ENGINE = InnoDB;
CREATE TABLE `payment_logs` ( `id_log` INT(11) NOT NULL AUTO_INCREMENT ,  `payment_id` INT(11) NOT NULL ,  `additional_info` TEXT NOT NULL ,  `status` ENUM('pending','paid','failed') NOT NULL ,  `created_at` DATETIME NOT NULL ,    PRIMARY KEY  (`id_log`)) ENGINE = InnoDB;
